__author__ = 'zhanghe'


import base64


def save_img_base64(str_base64, img_name):
    """
    将base64编码的字符串还原为图片保存
    :param str_base64:
    :param img_name:
    :return:
    """
    img_data = base64.b64decode(str_base64)
    with open(img_name, 'wb') as f:
        f.write(img_data)


if __name__ == '__main__':
    # data:image/png;base64,
    strs = ''
    save_img_base64(strs, '01.png')
